class Tax{Taxable(t,s){return parseFloat(t)*parseFloat(s)}Gst(t,s){return parseFloat(t)*parseFloat(s)/100}GstTotal(t,s){return parseFloat(t)+parseFloat(this.Gst(t,s))}GstIncludes(t,s){let a=this.Gst(t,s)*(100/(100+s));return{rate:t,taxPercent:s,taxAmount:a,taxable:t-a}}GstExcludes(t,s){return{rate:t,taxPercent:s,taxAmount:t*(s/100),taxable:this.GstTotal(t,s)}}}const gst=new Tax;
